const d = new Date();
const days = ["Minggu", "Senin", "Selasa", "Rabu", "Kamis", "Jumat", "Sabtu"];
document.getElementById("today").innerHTML = days[d.getDay()];
const months = ["Januari", "Februari", "Maret", "April", "Mei", "Juni", "Juli", "Agustus", "September", "Oktober", "November", "Desember"];
document.getElementById("tgl").innerHTML = d.getDate();
document.getElementById("bulan").innerHTML = months[d.getMonth()];
document.getElementById("tahun").innerHTML = d.getFullYear();
document.getElementById("jam").innerHTML = d.getHours();
document.getElementById("menit").innerHTML = d.getMinutes();
document.getElementById("detik").innerHTML = d.getSeconds();